public class Part2 {
    public int howMany(String stringa, String stringb){
        int startIndex = -stringb.length();
        int count = 0;
        while (true){
            startIndex = stringa.indexOf(stringb, startIndex + stringb.length());
            if (startIndex == -1){
                break;
            }
            count = count + 1;
        }
        return count;
    }
    
    public void testHowMany(){
        String dna = "";
        int resultTAT = howMany(dna, "CTG");
        int resultGCG = howMany(dna, "GCG");
        int resultATG = howMany(dna, "ATG");
        int resultAGC = howMany(dna, "AGC");
        System.out.println(resultTAT);
        System.out.println(resultGCG);
        System.out.println(resultATG);
        System.out.println(resultAGC);
    }
}

