    public StorageResource getAllGenes(String dna){
        StorageResource geneList = new StorageResource(); 
        int startIndex = 0;
        String gene;
        while (true) {
            gene = findGene(dna, startIndex);
            if (gene.isEmpty()){
                break;
            }
            geneList.add(gene);
            startIndex = dna.indexOf("ATG", startIndex) + gene.length();
        }
        return geneList;
    }
    
