    public double cgRatio(String dna){
        int count = 0;
        System.out.println(dna);
        for (int i = 0; i < dna.length(); i++){
            if ((dna.charAt(i) == 'C') || (dna.charAt(i) == 'G')){
                count++;
            }
            // System.out.println("i = " + i);
            // System.out.println("Char =" + dna.charAt(i));
            // System.out.println("countC = " + countC);
            // System.out.println("countG = " + countG);
        }
        double ratio = (float) count / dna.length();
        return ratio;
    }
    
    public void testCGRatio(){
        String dna = "ATACGC";
        System.out.println(cgRatio(dna));
    }
    
    public void processGenes(StorageResource sr) {
        int countGene = 0;
        int countLongGene = 0;
        int countHighCGRatio = 0;
        int longestGeneLength = 0;
        double cgRatioValue;
        StorageResource longGenes = new StorageResource();
        StorageResource highCGRatioGenes = new StorageResource();
        // Store values
        for (String gene:sr.data()){
            countGene++;
            if (gene.length() > 60){
                longGenes.add(gene);
                if (gene.length() > longestGeneLength){
                    longestGeneLength = gene.length();
                }
                countLongGene++;
            }
            
            
            cgRatioValue = cgRatio(gene);
            if (cgRatioValue > 0.35){
                highCGRatioGenes.add(gene);
                countHighCGRatio++;
            }
            
        }
        // Print long genes
        System.out.println("Genes longer than 9 nodes: ");
        for (String gene:longGenes.data()){
            System.out.println(gene);
        }
        System.out.println("Total number of long genes: " + countLongGene);
        // Print high CG ratio genes
        System.out.println("Genes with high CG ratio: ");
        for (String gene:highCGRatioGenes.data()){
            System.out.println(gene);
        }
        System.out.println("Total number of high CG ratio genes: " + countHighCGRatio);
        // Print longest gene
        System.out.println("Length of longest gene: ");
        System.out.println(longestGeneLength);
        System.out.println("Total number of genes: " + countGene);
    }
